Scripts.OnHide:
if ( SCCNScrollDown.isMoving ) then
SCCNScrollDown:StopMovingOrSizing();
SCCNScrollDown.isMoving = false;
end

Scripts.OnMouseDown:
if ( ( ( not SCCNScrollDown.isLocked ) or ( SCCNScrollDown.isLocked == 0 ) ) and ( arg1 == "LeftButton" ) ) and IsControlKeyDown() then
SCCNScrollDown:StartMoving();
SCCNScrollDown.isMoving = true;
end

Scripts.OnMouseUp:
if ( SCCNScrollDown.isMoving ) then
SCCNScrollDown:StopMovingOrSizing();
SCCNScrollDown.isMoving = false;
SCCNScrollDown_X = SCCNScrollDown:GetRight();
SCCNScrollDown_Y = SCCNScrollDown:GetBottom();
end

Scripts.OnClick:
if not IsControlKeyDown() then
local frame = "";
for i=1,7 do
frame = getglobal("ChatFrame"..i)
if frame then
frame:ScrollToBottom();
end
end
SCCNScrollDown:Hide();
end
